using AoC2022;

namespace AoC2022Tests.Day06;

public class Day06Tests
{
    [Theory]
    [InlineData("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 7)]
    [InlineData("bvwbjplbgvbhsrlpgdmjqwftvncz", 5)]
    [InlineData("nppdvjthqldpwncqszvftbrmjlhg", 6)]
    [InlineData("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 10)]
    [InlineData("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 11)]
    [InlineData("", 1909)]
    [InlineData("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 19, HandheldDevice.MarkerType.StartOfMessage)]
    [InlineData("bvwbjplbgvbhsrlpgdmjqwftvncz", 23, HandheldDevice.MarkerType.StartOfMessage)]
    [InlineData("nppdvjthqldpwncqszvftbrmjlhg", 23, HandheldDevice.MarkerType.StartOfMessage)]
    [InlineData("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", 29, HandheldDevice.MarkerType.StartOfMessage)]
    [InlineData("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", 26, HandheldDevice.MarkerType.StartOfMessage)]
    [InlineData("", 3380, HandheldDevice.MarkerType.StartOfMessage)]
    public void Test1(string datastreamBuffer, int firstMarkerAfterCharacterAt, HandheldDevice.MarkerType markerType = HandheldDevice.MarkerType.StartOfPacket)
    {
        var handheldDevice = new HandheldDevice(markerType);

        for (int i = 0; i < datastreamBuffer.Length && handheldDevice.FirstMarkerAppears is false; i++)
        {
            datastreamBuffer.ToList().ForEach(handheldDevice.ReceiveSignal);
        }

        handheldDevice.FirstMarkerAppearsAt.Should().Be(firstMarkerAfterCharacterAt);
    }
}